const menuIcon=document.querySelector(".menu-btn"),menu=document.querySelector(".nav-menu"),body=document.querySelector("html");menuIcon.addEventListener("click",(()=>{menu.classList.toggle("active"),body.classList.toggle("disable-scroll")}));const menuBtn=document.querySelector(".menu-btn");let menuOpen=!1;menuBtn.addEventListener("click",(()=>{menuOpen?(menuBtn.classList.remove("open"),menuOpen=!1):(menuBtn.classList.add("open"),menuOpen=!0)})),menu.addEventListener("click",(e=>{e.target.closest(".nav-menu")||closeMenu()}));const menuLinks=document.querySelectorAll(".nav-menu a");function closeMenu(){menu.classList.remove("active"),body.classList.remove("disable-scroll"),menuOpen=!1,menuBtn.classList.remove("open")}menuLinks.forEach((e=>{e.addEventListener("click",(()=>{closeMenu()}))})),window.addEventListener("scroll",(function(){var e=document.getElementById("header-nav");window.scrollY>10?e.classList.add("bg-white"):e.classList.remove("bg-white")}));