function reveal(){for(var e=document.querySelectorAll(".reveal"),a=0;a<e.length;a++){var d=window.innerHeight;e[a].getBoundingClientRect().top<d-150?e[a].classList.add("active"):e[a].classList.remove("active")}}function animateElements(){document.querySelectorAll(".load, .load-delayed-100, .load-delayed-200, .load-delayed-400, .load-delayed-600 ").forEach((e=>{e.classList.add("animate")}))}window.addEventListener("scroll",reveal),window.addEventListener("load",(()=>{animateElements()}));